mapper.select:
select studentid, subjectid, score, student_name, classid
from score,
     student
where score.studentid = student_id;

select studentid, subjectid, score, student_name, classid
from score,
     student
where score.studentid = student_id
limit #{begin},#{size};

select studentid, subjectid, score, student_name, classid
from score,
     student
where score.studentid = student_id
  and student.student_id = #{studentid}
limit #{begin},#{size};

select count(*)
from score
where studentid = #{studentid};

mapper.delete:
delete
from score
where studentid = #{studentid}
  and subjectid = #{subjectid};

mapper.update:
update score
set score=#{score}
where studentid = #{studentid}
  and subjectid = #{subjectid};
